import React, { useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [predictions, setPredictions] = useState([]);
  const [albums, setAlbums] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [preview, setPreview] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError(null);

      // Создание превью изображения
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Пожалуйста, выберите файл');
      return;
    }

    setIsLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Ошибка сервера');
      }

      const data = await response.json();
      setPredictions(data.predictions);
      setAlbums(data.albums);
    } catch (err) {
      setError('Ошибка загрузки файла. Пожалуйста, попробуйте снова.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>📷 Классификатор изображений</h1>
        <p>Загрузите изображение для автоматической классификации</p>
      </header>

      <main className="main-content">
        <div className="upload-section">
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="file-input-container">
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept="image/*"
                  className="file-input"
                />
                <span className="file-input-button">Выбрать файл</span>
                <span className="file-name">{file ? file.name : 'Файл не выбран'}</span>
              </label>
            </div>
            <button
              type="submit"
              disabled={isLoading || !file}
              className={`upload-button ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? (
                <>
                  <span className="spinner"></span>
                  Обработка...
                </>
              ) : 'Анализировать'}
            </button>
          </form>

          {error && <div className="error-message">{error}</div>}
        </div>

        {preview && (
          <div className="image-preview">
            <h3>Предпросмотр:</h3>
            <img src={preview} alt="Preview" className="preview-image" />
          </div>
        )}

        {(predictions.length > 0 || albums.length > 0) && (
          <div className="results-section">
            <div className="results-column">
              <h2>Результаты анализа</h2>
              <div className="predictions-list">
                {predictions.map((pred, index) => (
                  <div key={index} className="prediction-item">
                    <span className="prediction-label">{pred.label.replace('_', ' ')}</span>
                    <div className="confidence-bar-container">
                      <div
                        className="confidence-bar"
                        style={{ width: `${pred.confidence * 100}%` }}
                      ></div>
                      <span className="confidence-value">{(pred.confidence * 100).toFixed(1)}%</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="results-column">
              <h2>Рекомендуемые альбомы</h2>
              <div className="albums-grid">
                {albums.map((album, index) => (
                  <div key={index} className="album-card">
                    <div className="album-icon">
                      {album === 'People' && '👥'}
                      {album === 'Car' && '🚗'}
                      {album === 'Animals' && '🐾'}
                      {album === 'Nature' && '🌿'}
                      {!['People', 'Car', 'Animals', 'Nature'].includes(album) && '📁'}
                    </div>
                    <span className="album-name">{album}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </main>

      <footer className="app-footer">
        <p>© {new Date().getFullYear()} Image Classifier | Все права защищены</p>
      </footer>
    </div>
  );
}

export default App;
