import React, { useState } from 'react';
import axios from 'axios';
import Slider from 'react-slick';
import './App.css';

function App() {
  const [albums, setAlbums] = useState(null);
  const [status, setStatus] = useState('');

  // Функция для загрузки изображений на сервер
  const handleFileUpload = async (event) => {
    const formData = new FormData();
    for (let i = 0; i < event.target.files.length; i++) {
      formData.append('images', event.target.files[i]);
    }

    setStatus('Загрузка...');

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setAlbums(response.data.albums);
      setStatus('');
    } catch (error) {
      setStatus('Ошибка загрузки');
    }
  };

  return (
    <div className="container">
      <h1>Автоматизированный альбом</h1>

      {/* Форма для загрузки изображений */}
      <form>
        <label htmlFor="file-upload">Выберите изображения:</label>
        <input
          type="file"
          id="file-upload"
          multiple
          accept="image/*"
          onChange={handleFileUpload}
        />
      </form>

      {/* Статус загрузки */}
      {status && <div className="status">{status}</div>}

      {/* Отображение карусели с изображениями и описаниями */}
      {albums && Object.keys(albums).map((albumName) => (
        <div key={albumName}>
          <h2>{albumName}</h2>
          {/* Вывод описания альбома, если оно есть */}
          <p>Описание: {albumName}</p>

          <Slider
            infinite={true}
            speed={500}
            slidesToShow={1}
            slidesToScroll={1}
            dots={true}
            arrows={true}
            className="carousel"
          >
            {albums[albumName].map((imageUrl, index) => (
              <div key={index}>
                {/* Убедитесь, что путь к изображению правильный */}
                <img
                  src={`http://localhost:5000${imageUrl}`}
                  alt={`image-${index}`}
                  className="carousel-image"
                />
              </div>
            ))}
          </Slider>
        </div>
      ))}
    </div>
  );
}

export default App;
