import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [albums, setAlbums] = useState({});

    useEffect(() => {
        fetchAlbums();
    }, []);

    const handleFileChange = (event) => {
        setSelectedFiles([...event.target.files]);
    };

    const handleUpload = async () => {
        if (selectedFiles.length === 0) return;

        const formData = new FormData();
        selectedFiles.forEach(file => formData.append('files', file));

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' }
            });
            fetchAlbums(); // Обновляем альбомы после загрузки
        } catch (error) {
            console.error('Error uploading files', error);
        }
    };

    const fetchAlbums = async () => {
        try {
            const response = await axios.get('http://localhost:5000/albums');
            setAlbums(response.data.albums);
        } catch (error) {
            console.error('Error fetching albums', error);
        }
    };

    return (
        <div>
            <h1>Image Classification & Album Grouping</h1>
            <input type='file' multiple onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload</button>

            <h2>Albums</h2>
            {Object.keys(albums).map(category => (
                <div key={category}>
                    <h3>{category}</h3>
                    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                        {albums[category].map((imagePath, index) => (
                            <img
                                key={index}
                                src={`http://localhost:5000/${imagePath}`}
                                alt={category}
                                style={{ width: '100px', height: '100px', margin: '5px' }}
                            />
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default App;
