import React, { useState, useEffect } from "react";
import { Moon, Sun, Search, X } from "lucide-react";
import { Dialog } from "@headlessui/react";
import "./index.css"; // Убедись, что Tailwind подключён

const albumsData = [
  {
    id: 1,
    title: "Машины",
    images: ["/images/car1.jpg", "/images/car2.jpg", "/images/car3.jpg"],
  },
  {
    id: 2,
    title: "Люди",
    images: ["/images/people1.jpg", "/images/people2.jpg", "/images/people3.jpg"],
  },
  {
    id: 3,
    title: "Природа",
    images: ["/images/nature1.jpg", "/images/nature2.jpg", "/images/nature3.jpg"],
  },
];

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedImage, setSelectedImage] = useState(null);
  const [filteredAlbums, setFilteredAlbums] = useState(albumsData);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  useEffect(() => {
    const query = search.toLowerCase();
    const filtered = albumsData
      .map((album) => ({
        ...album,
        images: album.images.filter((img) => img.toLowerCase().includes(query)),
      }))
      .filter((album) => album.title.toLowerCase().includes(query) || album.images.length > 0);
    setFilteredAlbums(filtered);
  }, [search]);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 p-6 transition-colors duration-300">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Мои Альбомы</h1>
        <div className="flex gap-4 items-center">
          <div className="relative">
            <Search className="absolute left-2 top-2.5 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Поиск по альбомам..."
              className="pl-8 pr-4 py-2 rounded-lg border dark:border-gray-700 bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-blue-400"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="p-2 rounded-full border dark:border-gray-700 hover:bg-gray-200 dark:hover:bg-gray-800"
            aria-label="Toggle theme"
          >
            {darkMode ? <Sun /> : <Moon />}
          </button>
        </div>
      </div>

      <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredAlbums.map((album) => (
          <div
            key={album.id}
            className="bg-white dark:bg-gray-800 rounded-2xl shadow hover:shadow-lg transition-shadow duration-300 overflow-hidden"
          >
            <div className="relative h-48 w-full overflow-hidden">
              <img
                src={album.images[0]}
                alt={album.title}
                className="object-cover w-full h-full hover:scale-105 transition-transform duration-300 cursor-pointer"
                onClick={() => setSelectedImage(album.images[0])}
              />
              <div className="absolute inset-0 bg-black bg-opacity-10 dark:bg-opacity-30"></div>
            </div>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-2">{album.title}</h2>
              <div className="flex space-x-2 overflow-x-auto scrollbar-hide">
                {album.images.map((img, idx) => (
                  <img
                    key={idx}
                    src={img}
                    alt={`image-${idx}`}
                    onClick={() => setSelectedImage(img)}
                    className="w-16 h-16 object-cover rounded-lg cursor-pointer hover:ring-2 hover:ring-blue-400 transition"
                  />
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>

      <Dialog open={!!selectedImage} onClose={() => setSelectedImage(null)} className="fixed inset-0 z-50">
        <div className="fixed inset-0 bg-black/70" aria-hidden="true" />
        <div className="fixed inset-0 flex items-center justify-center p-4">
          <Dialog.Panel className="bg-white dark:bg-gray-800 rounded-xl shadow-lg overflow-hidden max-w-3xl w-full relative">
            <button
              onClick={() => setSelectedImage(null)}
              className="absolute top-4 right-4 p-2 text-gray-700 dark:text-gray-200 hover:text-red-500"
              aria-label="Закрыть"
            >
              <X />
            </button>
            <img src={selectedImage} alt="fullscreen" className="w-full h-full object-contain max-h-[80vh]" />
          </Dialog.Panel>
        </div>
      </Dialog>
    </div>
  );
}

export default App;
