import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏');
    const [progress, setProgress] = useState(0);
    const fileInputRef = useRef(null);

    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            if (!response.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞–ª—å–±–æ–º–æ–≤');
            const data = await response.json();
            setAlbums(data);
        } catch (error) {
            setStatus(`–û—à–∏–±–∫–∞: ${error.message}`);
        }
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = fileInputRef.current.files;

        if (!files || files.length === 0) {
            setStatus('–§–∞–π–ª—ã –Ω–µ –≤—ã–±—Ä–∞–Ω—ã');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]);
        }

        setStatus('–ó–∞–≥—Ä—É–∑–∫–∞...');
        setProgress(30);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞');
            }

            const result = await response.json();
            setProgress(70);
            await fetchAlbums();

            setStatus(`–£—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ ${files.length} —Ñ–æ—Ç–æ`);
            setProgress(100);
            fileInputRef.current.value = '';

            setTimeout(() => {
                setProgress(0);
                setStatus('–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏');
            }, 3000);
        } catch (error) {
            setStatus(`–û—à–∏–±–∫–∞: ${error.message}`);
            setProgress(0);
        }
    };

    const getEmojiForAlbum = (albumName) => {
        const album = albumName.toLowerCase();
        if (album.includes('people')) return 'üë•';
        if (album.includes('animal')) return 'üê∂';
        if (album.includes('car')) return 'üöó';
        if (album.includes('nature')) return 'üå≥';
        return 'üì∑';
    };

    const getAlbumColor = (albumName) => {
        const album = albumName.toLowerCase();
        if (album.includes('people')) return 'var(--people)';
        if (album.includes('animal')) return 'var(--animals)';
        if (album.includes('car')) return 'var(--transport)';
        if (album.includes('nature')) return 'var(--nature)';
        return 'var(--primary)';
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>–£–º–Ω—ã–µ –§–æ—Ç–æ–∞–ª—å–±–æ–º—ã</h1>
                <p className="app-subtitle">–§–æ—Ç–æ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å–æ—Ä—Ç–∏—Ä—É—é—Ç—Å—è –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º</p>
            </header>

            <form className="upload-form" onSubmit={handleUpload} encType="multipart/form-data">
                <div className="upload-area">
                    <label htmlFor="fileInput" className="upload-label">
                        <span className="upload-icon">üìÅ</span>
                        <span>–í—ã–±—Ä–∞—Ç—å —Ñ–æ—Ç–æ</span>
                        <input
                            id="fileInput"
                            type="file"
                            name="file"
                            multiple
                            accept="image/*"
                            ref={fileInputRef}
                            onChange={(e) => setStatus(
                                e.target.files.length > 0
                                    ? `–í—ã–±—Ä–∞–Ω–æ ${e.target.files.length} —Ñ–æ—Ç–æ`
                                    : '–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏'
                            )}
                            hidden
                        />
                    </label>
                    <button type="submit" className="upload-button">
                        –ó–∞–≥—Ä—É–∑–∏—Ç—å
                    </button>
                </div>

                <div className="status-area">
                    <span className="status-text">{status}</span>
                    {progress > 0 && (
                        <div className="progress-bar">
                            <div
                                className="progress-fill"
                                style={{ width: `${progress}%` }}
                            />
                        </div>
                    )}
                </div>
            </form>

            <section className="albums-container">
                {Object.keys(albums).length === 0 ? (
                    <div className="empty-albums">
                        <p>–ó–∞–≥—Ä—É–∑–∏—Ç–µ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏, —á—Ç–æ–±—ã —Å–æ–∑–¥–∞—Ç—å –∞–ª—å–±–æ–º—ã</p>
                        <div className="features-list">
                            <p>–°–∏—Å—Ç–µ–º–∞ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –æ—Ç—Å–æ—Ä—Ç–∏—Ä—É–µ—Ç:</p>
                            <ul>
                                <li><span>üë•</span> –õ—é–¥–∏</li>
                                <li><span>üöó</span> –ê–≤—Ç–æ–º–æ–±–∏–ª–∏</li>
                                <li><span>üê∂</span> –ñ–∏–≤–æ—Ç–Ω—ã–µ</li>
                                <li><span>üå≥</span> –ü—Ä–∏—Ä–æ–¥–∞</li>
                            </ul>
                        </div>
                    </div>
                ) : (
                    Object.entries(albums).map(([albumName, images]) => (
                        <div
                            key={albumName}
                            className="album-card"
                            style={{ borderLeft: `4px solid ${getAlbumColor(albumName)}` }}
                        >
                            <h2>
                                <span className="album-emoji">{getEmojiForAlbum(albumName)}</span>
                                {albumName}
                            </h2>
                            <div className="album-images">
                                {images.map((image) => (
                                    <div key={image.url} className="image-wrapper">
                                        <img
                                            src={`http://localhost:5000${image.url}`}
                                            alt={image.filename}
                                            className="album-image"
                                            onError={(e) => {
                                                e.target.onerror = null;
                                                e.target.src = '/image-error.png';
                                            }}
                                            loading="lazy"
                                        />
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))
                )}
            </section>
        </div>
    );
}

export default App;
