import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import './App.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('Файл не выбран');

    // Загрузка списка альбомов при монтировании компонента
    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            const data = await response.json();
            setAlbums(data);
        } catch (error) {
            console.error('Ошибка загрузки альбомов:', error);
        }
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = event.target.elements.images.files;

        if (!files.length) {
            setStatus('Файлы не выбраны');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]); // Изменено с 'images' на 'file' для соответствия бэкенду
        }

        setStatus(`Загрузка ${files.length} файл(ов)...`);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Ошибка сервера');
            }

            await response.json(); // Получаем результат загрузки
            await fetchAlbums(); // Обновляем список альбомов
            setStatus('Загрузка завершена');
        } catch (error) {
            console.error('Ошибка загрузки:', error);
            setStatus('Ошибка при загрузке');
        }
    };

    const sliderSettings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 3, // Показываем 3 изображения одновременно
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                },
            },
        ],
    };

    return (
        <div className="container">
            <h1>Фотоальбомы</h1>
            <form id="uploadForm" onSubmit={handleUpload}>
                <label htmlFor="fileInput" className="file-label">
                    Выбрать фотографии
                </label>
                <input
                    id="fileInput"
                    type="file"
                    name="images"
                    multiple
                    onChange={(e) => setStatus(`${e.target.files.length} файл(ов) выбран(о)`)}
                    hidden
                />
                <div className="status">{status}</div>
                <button type="submit">Загрузить</button>
            </form>
            <div id="albums">
                {Object.keys(albums).length === 0 ? (
                    <p>Альбомы пока не созданы</p>
                ) : (
                    Object.entries(albums).map(([albumName, images], idx) => (
                        <div key={idx} className="album">
                            <h2>{albumName}</h2>
                            <Slider {...sliderSettings}>
                                {images.map((filename, index) => (
                                    <div key={index}>
                                        <img
                                            src={`http://localhost:5000/uploads/${albumName}/${filename}`}
                                            alt={`${albumName}-${index}`}
                                            className="carousel-image"
                                        />
                                    </div>
                                ))}
                            </Slider>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

export default App;
