import React, { useState } from 'react';
import Slider from 'react-slick';
import './App.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('Файл не выбран');

    const handleUpload = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        const files = event.target.elements.images.files;

        for (let i = 0; i < files.length; i++) {
            formData.append('images', files[i]);
        }

        setStatus(`${files.length} файл(ов) выбран(о)`);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            const data = await response.json();
            setAlbums(data.albums);
            setStatus('Файл не выбран');
        } catch (error) {
            console.error('Ошибка загрузки:', error);
            setStatus('Ошибка при загрузке');
        }
    };

    const sliderSettings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
    };

    return (
        <div className="container">
            <h1>Загрузить фото</h1>
            <form id="uploadForm" onSubmit={handleUpload}>
                <label htmlFor="fileInput">Выбрать файлы</label>
                <input id="fileInput" type="file" name="images" multiple onChange={(e) => setStatus(`${e.target.files.length} файл(ов) выбран(о)`)} hidden />
                <div className="status">{status}</div>
                <button type="submit">Вывести альбомы</button>
            </form>
            <div id="albums">
                {Object.keys(albums).map((category, idx) => (
                    <div key={idx}>
                        <h2>{category}</h2>
                        <Slider {...sliderSettings}>
                            {albums[category].map((imgUrl, index) => (
                                <div key={index}>
                                    <img src={`http://localhost:5000${imgUrl}`} alt={`album-${index}`} className="carousel-image" />
                                </div>
                            ))}
                        </Slider>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
