import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('Выберите фотографии');
    const fileInputRef = useRef(null);

    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            const data = await response.json();
            setAlbums(data);
        } catch (error) {
            setStatus('Ошибка загрузки альбомов');
        }
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = fileInputRef.current.files;

        if (!files || files.length === 0) {
            setStatus('Файлы не выбраны');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]);
        }

        setStatus('Загрузка...');

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();
            await fetchAlbums();
            setStatus(`Загружено ${files.length} фото`);
            fileInputRef.current.value = '';
        } catch (error) {
            setStatus('Ошибка загрузки');
        }
    };

    const getEmoji = (album) => {
        const lcAlbum = album.toLowerCase();
        if (lcAlbum.includes('people')) return '👥';
        if (lcAlbum.includes('animal')) return '🐶';
        if (lcAlbum.includes('transport') || lcAlbum.includes('car')) return '🚗';
        if (lcAlbum.includes('nature')) return '🌳';
        return '📷';
    };

    return (
        <div className="app-container">
            <header>
                <h1>Мульти-категорийный альбом</h1>
                <p>Фото могут принадлежать нескольким категориям одновременно</p>
            </header>

            <form onSubmit={handleUpload}>
                <input
                    type="file"
                    multiple
                    ref={fileInputRef}
                    onChange={(e) => setStatus(`Выбрано ${e.target.files?.length || 0} фото`)}
                />
                <button type="submit">Загрузить</button>
                <p>{status}</p>
            </form>

            <div className="albums-grid">
                {Object.entries(albums).map(([album, images]) => (
                    <div key={album} className="album-card">
                        <h2>
                            <span>{getEmoji(album)}</span>
                            {album}
                        </h2>
                        <div className="images-container">
                            {images.map((img) => (
                                <div key={img.url} className="image-wrapper">
                                    <img
                                        src={`http://localhost:5000${img.url}`}
                                        alt={img.filename}
                                        onError={(e) => e.target.style.display = 'none'}
                                    />
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
