import React, { useState, useEffect, useRef } from 'react';
import Slider from 'react-slick';
import './App.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏');
    const fileInputRef = useRef(null);

    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            const data = await response.json();
            setAlbums(data);
        } catch (error) {
            setStatus('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞–ª—å–±–æ–º–æ–≤');
        }
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = fileInputRef.current.files;

        if (!files || files.length === 0) {
            setStatus('–§–∞–π–ª—ã –Ω–µ –≤—ã–±—Ä–∞–Ω—ã');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]);
        }

        setStatus('–ó–∞–≥—Ä—É–∑–∫–∞...');

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            const result = await response.json();
            await fetchAlbums();
            setStatus(`–ó–∞–≥—Ä—É–∂–µ–Ω–æ ${files.length} —Ñ–æ—Ç–æ`);
            fileInputRef.current.value = '';
        } catch (error) {
            setStatus('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏');
        }
    };

    const getEmojiForAlbum = (albumName) => {
        const album = albumName.toLowerCase();
        if (album.includes('people')) return 'üë•';
        if (album.includes('animal')) return 'üê∂';
        if (album.includes('transport') || album.includes('car')) return 'üöó';
        if (album.includes('nature')) return 'üå≥';
        return 'üì∑';
    };

    const sliderSettings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: Math.min(3, Object.keys(albums).length || 1),
        slidesToScroll: 1,
        responsive: [
            { breakpoint: 1024, settings: { slidesToShow: 2 } },
            { breakpoint: 600, settings: { slidesToShow: 1 } }
        ]
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>–£–º–Ω—ã–µ –§–æ—Ç–æ–∞–ª—å–±–æ–º—ã</h1>
                <p className="app-subtitle">–§–æ—Ç–æ –º–æ–≥—É—Ç –ø—Ä–∏–Ω–∞–¥–ª–µ–∂–∞—Ç—å –Ω–µ—Å–∫–æ–ª—å–∫–∏–º –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º</p>
            </header>

            <form className="upload-form" onSubmit={handleUpload} encType="multipart/form-data">
                <label htmlFor="fileInput" className="upload-label">
                    –í—ã–±—Ä–∞—Ç—å —Ñ–æ—Ç–æ
                </label>
                <input
                    id="fileInput"
                    type="file"
                    name="file"
                    multiple
                    ref={fileInputRef}
                    onChange={(e) => setStatus(`${e.target.files.length} –≤—ã–±—Ä–∞–Ω–æ`)}
                    hidden
                />
                <button type="submit" className="upload-button">
                    –ó–∞–≥—Ä—É–∑–∏—Ç—å
                </button>
                <span className="status-text">{status}</span>
            </form>

            <section className="albums-container">
                {Object.keys(albums).length === 0 ? (
                    <p className="empty-text">–ó–∞–≥—Ä—É–∑–∏—Ç–µ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏, —á—Ç–æ–±—ã —Å–æ–∑–¥–∞—Ç—å –∞–ª—å–±–æ–º—ã</p>
                ) : (
                    Object.entries(albums).map(([albumName, images]) => (
                        <div key={albumName} className="album-card">
                            <h2>
                                <span className="album-emoji">{getEmojiForAlbum(albumName)}</span>
                                {albumName}
                            </h2>
                            <Slider {...sliderSettings}>
                                {images.map((image, index) => (
                                    <div key={index} className="slide-item">
                                        <img
                                            src={`http://localhost:5000${image.url}`}
                                            alt={image.filename}
                                            className="album-image"
                                            onError={(e) => e.target.style.display = 'none'}
                                            loading="lazy"
                                        />
                                    </div>
                                ))}
                            </Slider>
                        </div>
                    ))
                )}
            </section>
        </div>
    );
}

export default App;
