import React, { useState, useEffect } from 'react';
import { FixedSizeList as List } from 'react-window';
import LinearProgress from '@mui/material/LinearProgress';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [albums, setAlbums] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [previews, setPreviews] = useState([]);
  const [theme, setTheme] = useState('light');
  const [uploadProgress, setUploadProgress] = useState(0);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  const processImage = (file) => {
    return new Promise((resolve) => {
      const img = new Image();
      const url = URL.createObjectURL(file);
      const MAX_PREVIEW_SIZE = 1024;

      img.onload = () => {
        const canvas = document.createElement('canvas');
        const scale = MAX_PREVIEW_SIZE / Math.max(img.width, img.height);
        canvas.width = img.width * scale;
        canvas.height = img.height * scale;

        const ctx = canvas.getContext('2d');
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

        resolve({
          id: URL.createObjectURL(file),
          url: canvas.toDataURL('image/jpeg', 0.7),
          name: file.name,
          file: file
        });
      };

      img.src = url;
    });
  };

  const handleFileChange = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length > 0) {
      setError(null);

      try {
        const previewPromises = selectedFiles.map(file => processImage(file));
        const newPreviews = await Promise.all(previewPromises);

        setFiles(selectedFiles);
        setPreviews(newPreviews);
      } catch (err) {
        setError('–û—à–∏–±–∫–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π');
        console.error(err);
      }
    }
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  if (files.length === 0) {
    setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —Ñ–∞–π–ª—ã');
    return;
  }

  setIsLoading(true);
  setUploadProgress(0);
  setError(null);
  setAlbums({});

  try {
    const formData = new FormData();
    previews.forEach(preview => {
      formData.append('files', preview.file);
    });

    const xhr = new XMLHttpRequest();
    xhr.open('POST', 'http://localhost:5000/upload', true);

    xhr.upload.onprogress = (event) => {
      if (event.lengthComputable) {
        const percent = Math.round((event.loaded / event.total) * 100);
        setUploadProgress(percent);
      }
    };

    xhr.onload = () => {
      if (xhr.status === 200) {
        const data = JSON.parse(xhr.responseText);

        const albumsData = {};
        data.forEach((result, index) => {
          result.albums.forEach(album => {
            if (!albumsData[album]) {
              albumsData[album] = [];
            }
            albumsData[album].push({
              id: previews[index].id,
              url: previews[index].url,
              name: result.filename
            });
          });
        });

        setAlbums(albumsData);
      } else {
        setError('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞');
        console.error(xhr.responseText);
      }
      setIsLoading(false);
    };

    xhr.onerror = () => {
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
      console.error(xhr.responseText);
      setIsLoading(false);
    };

    xhr.send(formData);
  } catch (err) {
    setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
    console.error(err);
    setIsLoading(false);
  }
};

  const downloadAlbum = async (albumName) => {
    try {
      const response = await fetch(`http://localhost:5000/download/${encodeURIComponent(albumName)}`);
      if (!response.ok) {
        throw new Error('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–∫–∞—á–∏–≤–∞–Ω–∏–∏ –∞–ª—å–±–æ–º–∞');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${albumName}.zip`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err) {
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å —Å–∫–∞—á–∞—Ç—å –∞–ª—å–±–æ–º');
      console.error(err);
    }
  };

  const getAlbumIcon = (albumName) => {
    const icons = {
      'People': 'üë•',
      'Car': 'üöó',
      'Animals': 'üêæ',
      'Nature': 'üåø',
      'Travel': '‚úàÔ∏è',
      'Food': 'üçï',
      'Sports': '‚öΩ',
      'Music': 'üéµ',
      'Art': 'üé®',
      'Beach': 'üèñÔ∏è',
      'Mountains': '‚õ∞Ô∏è',
      'City': 'üèôÔ∏è',
      'Wedding': 'üíç',
      'Birthday': 'üéÇ',
      'Family': 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶'
    };

    return icons[albumName] || 'üìÅ';
  };

  const PreviewRow = ({ index, style }) => (
    <div style={style}>
      <div className="preview-item">
        <img
          src={previews[index].url}
          alt={previews[index].name}
          className="preview-image"
          loading="lazy"
        />
        <span className="preview-name">{previews[index].name}</span>
      </div>
    </div>
  );

  return (
    <div className="app-container">
      <header className="app-header">
        <button className="theme-toggle" onClick={toggleTheme}>
          {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
        </button>
        <h1>üì∑ –ö–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ç–æ—Ä –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π</h1>
        <p>–ó–∞–≥—Ä—É–∑–∏—Ç–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–π —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏ –ø–æ –∞–ª—å–±–æ–º–∞–º</p>
      </header>

      <main className="main-content">
        {isLoading && (
          <div className="progress-bar-container">
            <LinearProgress
              variant="determinate"
              value={uploadProgress}
              color="primary"
            />
            <span>{uploadProgress}%</span>
          </div>
        )}

        <div className="upload-section">
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="file-input-container">
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept="image/*"
                  className="file-input"
                  multiple
                />
                <span className="file-input-button">
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                    <polyline points="17 8 12 3 7 8"></polyline>
                    <line x1="12" y1="3" x2="12" y2="15"></line>
                  </svg>
                  –í—ã–±—Ä–∞—Ç—å —Ñ–∞–π–ª—ã
                </span>
                <span className="file-name">
                  {files.length > 0 ? `${files.length} —Ñ–∞–π–ª(–æ–≤) –≤—ã–±—Ä–∞–Ω–æ` : '–§–∞–π–ª—ã –Ω–µ –≤—ã–±—Ä–∞–Ω—ã'}
                </span>
              </label>
            </div>
            <button
              type="submit"
              disabled={isLoading || files.length === 0}
              className={`upload-button ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? (
                <>
                  <span className="spinner"></span>
                  –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞...
                </>
              ) : (
                <>
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                    <polyline points="17 8 12 3 7 8"></polyline>
                    <line x1="12" y1="3" x2="12" y2="15"></line>
                  </svg>
                  –°–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø–æ –∞–ª—å–±–æ–º–∞–º
                </>
              )}
            </button>
          </form>

          {error && <div className="error-message">{error}</div>}
        </div>

        {previews.length > 0 && (
          <div className="previews-section">
            <h3>–í—ã–±—Ä–∞–Ω–Ω—ã–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è ({previews.length}):</h3>
            <List
              height={400}
              itemCount={previews.length}
              itemSize={180}
              width="100%"
            >
              {PreviewRow}
            </List>
          </div>
        )}

        {Object.keys(albums).length > 0 && (
          <div className="albums-section">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏</h2>
            <div className="albums-container">
              {Object.entries(albums).map(([albumName, photos]) => (
                <div key={albumName} className="album-card" style={{ animationDelay: `${0.1 * Object.keys(albums).indexOf(albumName)}s` }}>
                  <div className="album-header">
                    <div className="album-icon">
                      {getAlbumIcon(albumName)}
                    </div>
                    <h3 className="album-title">{albumName}</h3>
                    <div className="album-actions">
                      <span className="album-count">{photos.length} —Ñ–æ—Ç–æ</span>
                      <button
                        onClick={() => downloadAlbum(albumName)}
                        className="download-button"
                        title="–°–∫–∞—á–∞—Ç—å –∞–ª—å–±–æ–º"
                      >
                        <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                          <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                          <polyline points="7 10 12 15 17 10"></polyline>
                          <line x1="12" y1="15" x2="12" y2="3"></line>
                        </svg>
                      </button>
                    </div>
                  </div>
                  <div className="album-photos">
                    {photos.map(photo => (
                      <div key={photo.id} className="photo-item">
                        <img src={photo.url} alt={photo.name} className="album-photo" />
                        <span className="photo-name">{photo.name}</span>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      <footer className="app-footer">
        <p>¬© {new Date().getFullYear()} Image Classifier | –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∞—è —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–π</p>
      </footer>
    </div>
  );
}

export default App;
