// frontend/src/App.js
import React, { useState, useEffect, useRef } from 'react';
import Slider from 'react-slick';
import './App.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('Выберите фотографии');
    const fileInputRef = useRef(null); // Реф для управления input

    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            if (!response.ok) throw new Error('Ошибка загрузки альбомов');
            const data = await response.json();
            console.log('Полученные альбомы:', data);
            setAlbums(data);
        } catch (error) {
            console.error('Ошибка загрузки альбомов:', error);
            setStatus('Ошибка загрузки альбомов');
        }
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = event.target.elements.images.files;

        if (!files.length) {
            setStatus('Файлы не выбраны');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]);
        }

        setStatus(`Загрузка ${files.length} фото...`);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) throw new Error('Ошибка сервера');
            const result = await response.json();
            console.log('Результат загрузки:', result);
            await fetchAlbums();
            setStatus(`Загружено ${result.length} фото`);

            // Сбрасываем форму и очищаем input
            event.target.reset();
            fileInputRef.current.value = null;
        } catch (error) {
            console.error('Ошибка загрузки:', error);
            setStatus('Ошибка при загрузке');
        }
    };

    const sliderSettings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [
            { breakpoint: 1024, settings: { slidesToShow: 2 } },
            { breakpoint: 768, settings: { slidesToShow: 1 } }
        ]
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>Фотоальбомы</h1>
            </header>
            <main>
                <form className="upload-form" onSubmit={handleUpload}>
                    <label htmlFor="fileInput" className="upload-label">
                        Выбрать фото
                    </label>
                    <input
                        id="fileInput"
                        type="file"
                        name="images"
                        multiple
                        ref={fileInputRef} // Привязываем реф
                        onChange={(e) => setStatus(`${e.target.files.length} выбрано`)}
                        hidden
                    />
                    <span className="status-text">{status}</span>
                    <button type="submit" className="upload-button">
                        Загрузить
                    </button>
                </form>
                <section className="albums-container">
                    {Object.keys(albums).length === 0 ? (
                        <p className="empty-text">Альбомы отсутствуют</p>
                    ) : (
                        Object.entries(albums).map(([albumName, images], idx) => (
                            <div key={idx} className="album-card">
                                <h2>{albumName}</h2>
                                <Slider {...sliderSettings}>
                                    {images.map((image, index) => (
                                        <div key={index} className="slide-item">
                                            <img
                                                src={`http://localhost:5000${image.url}`}
                                                alt={`${albumName}-${image.filename}`}
                                                className="album-image"
                                                onError={(e) => console.error(`Ошибка загрузки изображения: ${image.url}`)}
                                            />
                                        </div>
                                    ))}
                                </Slider>
                            </div>
                        ))
                    )}
                </section>
            </main>
        </div>
    );
}

export default App;
