import React, { useState, useEffect, useRef } from 'react';
import Slider from 'react-slick';
import './App.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('Выберите фотографии');
    const fileInputRef = useRef(null);

    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            if (!response.ok) throw new Error('Ошибка загрузки альбомов');
            const data = await response.json();
            setAlbums(data);
        } catch (error) {
            setStatus('Ошибка загрузки альбомов');
        }
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = fileInputRef.current.files;

        if (!files || files.length === 0) {
            setStatus('Файлы не выбраны');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]);  // Ключ 'file' должен совпадать с бэкендом
        }

        setStatus(`Загрузка ${files.length} фото...`);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
                // Не указываем Content-Type вручную!
            });

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText);
            }

            const result = await response.json();
            await fetchAlbums();
            setStatus('Фотографии успешно загружены!');
            fileInputRef.current.value = '';  // Сбрасываем input
        } catch (error) {
            setStatus(`Ошибка загрузки: ${error.message}`);
        }
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>Ваши Фотоальбомы</h1>
            </header>
            <main>
                <form className="upload-form" onSubmit={handleUpload}>
                    <label htmlFor="fileInput" className="upload-label">
                        Выбрать фото
                    </label>
                    <input
                        id="fileInput"
                        type="file"
                        name="images"
                        multiple
                        ref={fileInputRef}
                        onChange={(e) => setStatus(`${e.target.files.length} выбрано`)}
                        hidden
                    />
                    <span className="status-text">{status}</span>
                    <button type="submit" className="upload-button">
                        Загрузить
                    </button>
                </form>
                <section className="albums-container">
                    {Object.keys(albums).length === 0 ? (
                        <p className="empty-text">Загрузите фотографии, чтобы создать альбомы</p>
                    ) : (
                        Object.entries(albums).map(([albumName, images], idx) => (
                            <div key={idx} className="album-card">
                                <h2>{albumName}</h2>
                                <Slider {...sliderSettings}>
                                    {images.map((image, index) => (
                                        <div key={index} className="slide-item">
                                            <img
                                                src={`http://localhost:5000${image.url}?t=${Date.now()}`} // Добавлен timestamp для избежания кэша
                                                alt={`${albumName}-${image.filename}`}
                                                className="album-image"
                                                onError={(e) => {
                                                    console.error(`Ошибка загрузки изображения: ${image.url}`);
                                                    e.target.style.display = 'none';
                                                }}
                                            />
                                        </div>
                                    ))}
                                </Slider>
                            </div>
                        ))
                    )}
                </section>
            </main>
        </div>
    );
}

export default App;
