import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [albums, setAlbums] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [previews, setPreviews] = useState([]);
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length > 0) {
      setFiles(selectedFiles);
      setError(null);

      const newPreviews = [];
      selectedFiles.forEach(file => {
        const reader = new FileReader();
        reader.onloadend = () => {
          newPreviews.push({
            id: URL.createObjectURL(file),
            url: reader.result,
            name: file.name,
            file: file
          });
          if (newPreviews.length === selectedFiles.length) {
            setPreviews(newPreviews);
          }
        };
        reader.readAsDataURL(file);
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (files.length === 0) {
      setError('Пожалуйста, выберите файлы');
      return;
    }

    setIsLoading(true);
    setError(null);
    setAlbums({});

    try {
      const formData = new FormData();
      previews.forEach(preview => {
        formData.append('files', preview.file);
      });

      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Ошибка сервера');
      }

      const data = await response.json();

      const albumsData = {};
      data.forEach((result, index) => {
        result.albums.forEach(album => {
          if (!albumsData[album]) {
            albumsData[album] = [];
          }
          albumsData[album].push({
            id: previews[index].id,
            url: previews[index].url,
            name: result.filename
          });
        });
      });

      setAlbums(albumsData);
    } catch (err) {
      setError('Ошибка загрузки файлов. Пожалуйста, попробуйте снова.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const downloadAlbum = async (albumName) => {
    try {
      const response = await fetch(`http://localhost:5000/download/${encodeURIComponent(albumName)}`);
      if (!response.ok) {
        throw new Error('Ошибка при скачивании альбома');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${albumName}.zip`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err) {
      setError('Не удалось скачать альбом');
      console.error(err);
    }
  };

  const getAlbumIcon = (albumName) => {
    const icons = {
      'People': '👥',
      'Car': '🚗',
      'Animals': '🐾',
      'Nature': '🌿',
      'Travel': '✈️',
      'Food': '🍕',
      'Sports': '⚽',
      'Music': '🎵',
      'Art': '🎨',
      'Beach': '🏖️',
      'Mountains': '⛰️',
      'City': '🏙️',
      'Wedding': '💍',
      'Birthday': '🎂',
      'Family': '👨‍👩‍👧‍👦'
    };

    return icons[albumName] || '📁';
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <button className="theme-toggle" onClick={toggleTheme}>
          {theme === 'light' ? '🌙' : '☀️'}
        </button>
        <h1>📷 Классификатор изображений</h1>
        <p>Загрузите изображения для автоматической сортировки по альбомам</p>
      </header>

      <main className="main-content">
        <div className="upload-section">
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="file-input-container">
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept="image/*"
                  className="file-input"
                  multiple
                />
                <span className="file-input-button">
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                    <polyline points="17 8 12 3 7 8"></polyline>
                    <line x1="12" y1="3" x2="12" y2="15"></line>
                  </svg>
                  Выбрать файлы
                </span>
                <span className="file-name">
                  {files.length > 0 ? `${files.length} файл(ов) выбрано` : 'Файлы не выбраны'}
                </span>
              </label>
            </div>
            <button
              type="submit"
              disabled={isLoading || files.length === 0}
              className={`upload-button ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? (
                <>
                  <span className="spinner"></span>
                  Сортировка...
                </>
              ) : (
                <>
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                    <polyline points="17 8 12 3 7 8"></polyline>
                    <line x1="12" y1="3" x2="12" y2="15"></line>
                  </svg>
                  Сортировать по альбомам
                </>
              )}
            </button>
          </form>

          {error && <div className="error-message">{error}</div>}
        </div>

        {previews.length > 0 && (
          <div className="previews-section">
            <h3>Выбранные изображения ({previews.length}):</h3>
            <div className="previews-grid">
              {previews.map((preview) => (
                <div key={preview.id} className="preview-item">
                  <img src={preview.url} alt={preview.name} className="preview-image" />
                  <span className="preview-name">{preview.name}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {Object.keys(albums).length > 0 && (
          <div className="albums-section">
            <h2>Результаты сортировки</h2>
            <div className="albums-container">
              {Object.entries(albums).map(([albumName, photos]) => (
                <div key={albumName} className="album-card" style={{ animationDelay: `${0.1 * Object.keys(albums).indexOf(albumName)}s` }}>
                  <div className="album-header">
                    <div className="album-icon">
                      {getAlbumIcon(albumName)}
                    </div>
                    <h3 className="album-title">{albumName}</h3>
                    <div className="album-actions">
                      <span className="album-count">{photos.length} фото</span>
                      <button
                        onClick={() => downloadAlbum(albumName)}
                        className="download-button"
                        title="Скачать альбом"
                      >
                        <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                          <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                          <polyline points="7 10 12 15 17 10"></polyline>
                          <line x1="12" y1="15" x2="12" y2="3"></line>
                        </svg>
                      </button>
                    </div>
                  </div>
                  <div className="album-photos">
                    {photos.map(photo => (
                      <div key={photo.id} className="photo-item">
                        <img src={photo.url} alt={photo.name} className="album-photo" />
                        <span className="photo-name">{photo.name}</span>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      <footer className="app-footer">
        <p>© {new Date().getFullYear()} Image Classifier | Автоматическая сортировка фотографий</p>
      </footer>
    </div>
  );
}

export default App;
