import React, { useState, useEffect, useRef } from 'react';
import Slider from 'react-slick';
import './App.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

// Приоритетные альбомы для сортировки
const PRIORITY_ALBUMS = ['People', 'Animals', 'Nature'];

function App() {
    const [albums, setAlbums] = useState({});
    const [status, setStatus] = useState('Выберите фотографии');
    const [progress, setProgress] = useState(0);
    const fileInputRef = useRef(null);

    useEffect(() => {
        fetchAlbums();
    }, []);

    const fetchAlbums = async () => {
        try {
            const response = await fetch('http://localhost:5000/albums');
            if (!response.ok) throw new Error('Ошибка загрузки альбомов');
            const data = await response.json();
            setAlbums(sortAlbums(data));
        } catch (error) {
            setStatus('Ошибка загрузки альбомов');
        }
    };

    const sortAlbums = (albumsData) => {
        const sorted = {};

        // Сначала приоритетные альбомы
        PRIORITY_ALBUMS.forEach(album => {
            if (albumsData[album]) {
                sorted[album] = albumsData[album];
            }
        });

        // Затем остальные в алфавитном порядке
        Object.keys(albumsData)
            .filter(album => !PRIORITY_ALBUMS.includes(album))
            .sort()
            .forEach(album => {
                sorted[album] = albumsData[album];
            });

        return sorted;
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        const files = fileInputRef.current.files;

        if (!files || files.length === 0) {
            setStatus('Файлы не выбраны');
            return;
        }

        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('file', files[i]);
        }

        setStatus('Загрузка...');
        setProgress(0);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText);
            }

            const result = await response.json();
            await fetchAlbums();
            setStatus(`Успешно загружено ${files.length} фото`);
            setProgress(100);
            fileInputRef.current.value = '';

            // Сброс прогресса через 3 секунды
            setTimeout(() => setProgress(0), 3000);
        } catch (error) {
            setStatus(`Ошибка: ${error.message}`);
            setProgress(0);
        }
    };

    const sliderSettings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: Math.min(3, Object.keys(albums).length || 1),
        slidesToScroll: 1,
        responsive: [
            { breakpoint: 1024, settings: { slidesToShow: 2 } },
            { breakpoint: 600, settings: { slidesToShow: 1 } }
        ]
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>Умные Фотоальбомы</h1>
                <p className="app-subtitle">Фото автоматически сортируются по категориям</p>
            </header>

            <main>
                <form className="upload-form" onSubmit={handleUpload} encType="multipart/form-data">
                    <div className="upload-area">
                        <label htmlFor="fileInput" className="upload-label">
                            <span className="upload-icon">📷</span>
                            <span>Выбрать фото</span>
                            <input
                                id="fileInput"
                                type="file"
                                name="file"
                                multiple
                                accept="image/*"
                                ref={fileInputRef}
                                onChange={(e) => setStatus(
                                    e.target.files.length > 0
                                        ? `Выбрано ${e.target.files.length} фото`
                                        : 'Выберите фотографии'
                                )}
                                hidden
                            />
                        </label>
                        <button type="submit" className="upload-button">
                            Загрузить
                        </button>
                    </div>

                    <div className="status-area">
                        <span className="status-text">{status}</span>
                        {progress > 0 && (
                            <div className="progress-bar">
                                <div
                                    className="progress-fill"
                                    style={{ width: `${progress}%` }}
                                ></div>
                            </div>
                        )}
                    </div>
                </form>

                <section className="albums-container">
                    {Object.keys(albums).length === 0 ? (
                        <div className="empty-albums">
                            <p>Загрузите фотографии, чтобы создать альбомы</p>
                            <p>Система автоматически отсортирует:</p>
                            <ul>
                                <li>📌 Люди (обнаружение лиц)</li>
                                <li>🐶 Животные</li>
                                <li>🌳 Природа</li>
                                <li>🏠 Другие категории</li>
                            </ul>
                        </div>
                    ) : (
                        Object.entries(albums).map(([albumName, images], idx) => (
                            <div key={idx} className="album-card">
                                <h2>
                                    {albumName === 'People' && '👥 '}
                                    {albumName === 'Animals' && '🐶 '}
                                    {albumName === 'Nature' && '🌳 '}
                                    {albumName}
                                </h2>
                                <Slider {...sliderSettings}>
                                    {images.map((image, index) => (
                                        <div key={index} className="slide-item">
                                            <img
                                                src={`http://localhost:5000${image.url}?t=${Date.now()}`}
                                                alt={image.filename}
                                                className="album-image"
                                                loading="lazy"
                                                onError={(e) => {
                                                    e.target.style.display = 'none';
                                                }}
                                            />
                                        </div>
                                    ))}
                                </Slider>
                            </div>
                        ))
                    )}
                </section>
            </main>
        </div>
    );
}

export default App;
