import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Upload, Download, Sun, Moon, Trash2 } from 'lucide-react';
import './App.css';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState(null);
  const [albums, setAlbums] = useState({});
  const [isClearing, setIsClearing] = useState(false);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setDarkMode(true);
      document.documentElement.setAttribute('data-theme', 'dark');
    }
    fetchAlbums();
  }, []);

  const toggleTheme = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    if (newDarkMode) {
      document.documentElement.setAttribute('data-theme', 'dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.removeAttribute('data-theme');
      localStorage.setItem('theme', 'light');
    }
  };

  const fetchAlbums = async () => {
    try {
      const response = await axios.get('/albums');
      setAlbums(response.data);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞–ª—å–±–æ–º–æ–≤:', err);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFiles(Array.from(e.target.files));
    setError(null);
  };

  const handleUpload = async () => {
    if (selectedFiles.length === 0) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω —Ñ–∞–π–ª');
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach(file => formData.append('files', file));

    setIsUploading(true);
    setUploadProgress(0);

    try {
      await axios.post('/upload', formData, {
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded / progressEvent.total) * 100
          );
          setUploadProgress(progress);
        },
      });
      await fetchAlbums();
      setSelectedFiles([]);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error || '–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤');
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', err);
    } finally {
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const handleDownload = async (albumName) => {
    try {
      const response = await axios.get(`/download/${albumName}`, {
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${albumName}.zip`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞–ª—å–±–æ–º–∞');
      console.error('–û—à–∏–±–∫–∞ —Å–∫–∞—á–∏–≤–∞–Ω–∏—è:', err);
    }
  };

  const handleClearAll = async () => {
    if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å –≤—Å–µ —Ñ–æ—Ç–æ –∏ –∞–ª—å–±–æ–º—ã? –≠—Ç–æ –¥–µ–π—Å—Ç–≤–∏–µ –Ω–µ–ª—å–∑—è –æ—Ç–º–µ–Ω–∏—Ç—å.')) {
      setIsClearing(true);
      try {
        await axios.delete('/clear');
        setAlbums({});
        setError(null);
      } catch (err) {
        setError(err.response?.data?.error || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—á–∏—Å—Ç–∫–µ');
        console.error('–û—à–∏–±–∫–∞ –æ—á–∏—Å—Ç–∫–∏:', err);
      } finally {
        setIsClearing(false);
      }
    }
  };

  // –≠—Ñ—Ñ–µ–∫—Ç ripple –¥–ª—è –∫–Ω–æ–ø–æ–∫
  const createRipple = (event) => {
    const button = event.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(button.clientWidth, button.clientHeight);
    const radius = diameter / 2;

    circle.style.width = circle.style.height = `${diameter}px`;
    circle.style.left = `${event.clientX - button.getBoundingClientRect().left - radius}px`;
    circle.style.top = `${event.clientY - button.getBoundingClientRect().top - radius}px`;
    circle.classList.add('ripple');

    const ripple = button.getElementsByClassName('ripple')[0];
    if (ripple) ripple.remove();

    button.appendChild(circle);
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>–ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–π —Ñ–æ—Ç–æ–∞–ª—å–±–æ–º</h1>
        <p>–ó–∞–≥—Ä—É–∑–∏—Ç–µ —Ñ–æ—Ç–æ, –∏ –º—ã –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Ä–∞—Å—Å–æ—Ä—Ç–∏—Ä—É–µ–º –∏—Ö –ø–æ –∞–ª—å–±–æ–º–∞–º</p>
        <button
          className="icon-button theme-toggle"
          onClick={toggleTheme}
          aria-label="–ü–µ—Ä–µ–∫–ª—é—á–∏—Ç—å —Ç–µ–º—É"
        >
          {darkMode ? <Sun size={20} /> : <Moon size={20} />}
        </button>
      </header>

      <main className="main-content">
        {uploadProgress > 0 && (
          <div className="progress-bar-container">
            <progress value={uploadProgress} max="100" />
            <span>–ó–∞–≥—Ä—É–∑–∫–∞: {uploadProgress}%</span>
          </div>
        )}

        <section className="upload-section">
          <div className="upload-card">
            <h3>–í—ã–±—Ä–∞—Ç—å —Ñ–∞–π–ª—ã</h3>
            <div className="file-selection-area">
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  multiple
                  accept="image/*"
                />
                <div className="file-input-content">
                  <Upload size={24} className="upload-icon" />
                  <span>–ù–∞–∂–º–∏—Ç–µ –¥–ª—è –≤—ã–±–æ—Ä–∞ —Ñ–∞–π–ª–æ–≤</span>
                  <span className="file-hint">–∏–ª–∏ –ø–µ—Ä–µ—Ç–∞—â–∏—Ç–µ –∏—Ö —Å—é–¥–∞</span>
                </div>
              </label>
            </div>

            <div className="button-group">
              <button
                className={`primary-button ${isUploading ? 'loading' : ''}`}
                onClick={(e) => {
                  createRipple(e);
                  handleUpload();
                }}
                disabled={isUploading || selectedFiles.length === 0}
              >
                <span className="button-text">
                  {isUploading ? '–ó–∞–≥—Ä—É–∑–∫–∞...' : '–ó–∞–≥—Ä—É–∑–∏—Ç—å —Ñ–æ—Ç–æ'}
                </span>
                {isUploading ? (
                  <span className="spinner"></span>
                ) : (
                  <Upload size={18} />
                )}
              </button>

              {Object.keys(albums).length > 0 && (
                <button
                  className="danger-button"
                  onClick={(e) => {
                    createRipple(e);
                    handleClearAll();
                  }}
                  disabled={isClearing}
                >
                  <span className="button-text">
                    {isClearing ? '–£–¥–∞–ª–µ–Ω–∏–µ...' : '–û—á–∏—Å—Ç–∏—Ç—å –≤—Å–µ'}
                  </span>
                  {isClearing ? (
                    <span className="spinner"></span>
                  ) : (
                    <Trash2 size={18} />
                  )}
                </button>
              )}
            </div>

            {selectedFiles.length > 0 && (
              <div className="selected-files-info">
                <span>–í—ã–±—Ä–∞–Ω–æ —Ñ–∞–π–ª–æ–≤: {selectedFiles.length}</span>
              </div>
            )}
          </div>

          {error && <div className="error-message">{error}</div>}
        </section>

        {selectedFiles.length > 0 && (
          <section className="previews-section">
            <h3>–í—ã–±—Ä–∞–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã ({selectedFiles.length})</h3>
            <div className="previews-grid">
              {selectedFiles.map((file, index) => (
                <div key={index} className="preview-item">
                  <img
                    src={URL.createObjectURL(file)}
                    alt={file.name}
                    className="preview-image"
                  />
                  <span className="preview-name">{file.name}</span>
                </div>
              ))}
            </div>
          </section>
        )}

        <section className="albums-section">
          <h2>–í–∞—à–∏ –∞–ª—å–±–æ–º—ã</h2>
          {Object.keys(albums).length === 0 ? (
            <div className="empty-albums">
              <p>–ê–ª—å–±–æ–º–æ–≤ –ø–æ–∫–∞ –Ω–µ—Ç. –ó–∞–≥—Ä—É–∑–∏—Ç–µ —Ñ–æ—Ç–æ —á—Ç–æ–±—ã –Ω–∞—á–∞—Ç—å!</p>
            </div>
          ) : (
            <div className="albums-container">
              {Object.entries(albums).map(([albumName, photos]) => (
                <div key={albumName} className="album-card">
                  <div className="album-header">
                    <div className="album-icon">
                      {albumName === '–õ—é–¥–∏' ? 'üë•' :
                       albumName === '–ú–∞—à–∏–Ω—ã' ? 'üöó' :
                       albumName === '–ñ–∏–≤–æ—Ç–Ω—ã–µ' ? 'üêæ' :
                       albumName === '–ü—Ä–∏—Ä–æ–¥–∞' ? 'üåø' : 'üì∑'}
                    </div>
                    <h3 className="album-title">{albumName}</h3>
                    <div className="album-actions">
                      <span className="album-count">{photos.length} —Ñ–æ—Ç–æ</span>
                      <button
                        className="icon-button download-button"
                        onClick={(e) => {
                          createRipple(e);
                          handleDownload(albumName);
                        }}
                        title="–°–∫–∞—á–∞—Ç—å –∞–ª—å–±–æ–º"
                      >
                        <Download size={18} />
                      </button>
                    </div>
                  </div>
                  <div className="album-photos">
                    {photos.map((photo, index) => (
                      <div key={index} className="photo-item">
                        <img
                          src={photo.url}
                          alt={photo.filename}
                          className="album-photo"
                          loading="lazy"
                        />
                        <span className="photo-name">{photo.filename}</span>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      </main>

      <footer className="app-footer">
        <p>¬© {new Date().getFullYear()} –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–π —Ñ–æ—Ç–æ–∞–ª—å–±–æ–º</p>
      </footer>
    </div>
  );
}

export default App;
