import React, { useState } from 'react';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [previews, setPreviews] = useState([]);

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length > 0) {
      setFiles(selectedFiles);
      setError(null);

      // –°–æ–∑–¥–∞–Ω–∏–µ –ø—Ä–µ–≤—å—é –¥–ª—è –≤—Å–µ—Ö –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
      const newPreviews = [];
      selectedFiles.forEach(file => {
        const reader = new FileReader();
        reader.onloadend = () => {
          newPreviews.push({
            id: URL.createObjectURL(file), // —É–Ω–∏–∫–∞–ª—å–Ω—ã–π –∏–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä
            url: reader.result,
            name: file.name
          });
          if (newPreviews.length === selectedFiles.length) {
            setPreviews(newPreviews);
          }
        };
        reader.readAsDataURL(file);
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (files.length === 0) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —Ñ–∞–π–ª—ã');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      files.forEach(file => {
        formData.append('files', file); // 'files' –≤–º–µ—Å—Ç–æ 'file'
      });

      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞');
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>üì∑ –ö–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ç–æ—Ä –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π</h1>
        <p>–ó–∞–≥—Ä—É–∑–∏—Ç–µ –æ–¥–Ω–æ –∏–ª–∏ –Ω–µ—Å–∫–æ–ª—å–∫–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–π –∫–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ü–∏–∏</p>
      </header>

      <main className="main-content">
        <div className="upload-section">
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="file-input-container">
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept="image/*"
                  className="file-input"
                  multiple // –î–æ–±–∞–≤–ª—è–µ–º multiple –¥–ª—è –≤—ã–±–æ—Ä–∞ –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö —Ñ–∞–π–ª–æ–≤
                />
                <span className="file-input-button">–í—ã–±—Ä–∞—Ç—å —Ñ–∞–π–ª—ã</span>
                <span className="file-name">
                  {files.length > 0 ? `${files.length} —Ñ–∞–π–ª(–æ–≤) –≤—ã–±—Ä–∞–Ω–æ` : '–§–∞–π–ª—ã –Ω–µ –≤—ã–±—Ä–∞–Ω—ã'}
                </span>
              </label>
            </div>
            <button
              type="submit"
              disabled={isLoading || files.length === 0}
              className={`upload-button ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? (
                <>
                  <span className="spinner"></span>
                  –û–±—Ä–∞–±–æ—Ç–∫–∞...
                </>
              ) : '–ê–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞—Ç—å'}
            </button>
          </form>

          {error && <div className="error-message">{error}</div>}
        </div>

        {previews.length > 0 && (
          <div className="previews-grid">
            <h3>–í—ã–±—Ä–∞–Ω–Ω—ã–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è:</h3>
            <div className="previews-container">
              {previews.map((preview, index) => (
                <div key={preview.id} className="preview-item">
                  <img src={preview.url} alt={`Preview ${index}`} className="preview-image" />
                  <span className="preview-name">{preview.name}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {results.length > 0 && (
          <div className="results-section">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã –∞–Ω–∞–ª–∏–∑–∞</h2>
            <div className="results-grid">
              {results.map((result, index) => (
                <div key={index} className="result-card">
                  <div className="result-image">
                    <img src={previews[index]?.url} alt={`Result ${index}`} />
                  </div>
                  <div className="result-details">
                    <h3>{result.filename}</h3>

                    <div className="result-predictions">
                      <h4>–ö–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ü–∏—è:</h4>
                      {result.predictions.map((pred, i) => (
                        <div key={i} className="prediction-item">
                          <span className="prediction-label">{pred.label.replace('_', ' ')}</span>
                          <div className="confidence-bar-container">
                            <div
                              className="confidence-bar"
                              style={{ width: `${pred.confidence * 100}%` }}
                            ></div>
                            <span className="confidence-value">{(pred.confidence * 100).toFixed(1)}%</span>
                          </div>
                        </div>
                      ))}
                    </div>

                    <div className="result-albums">
                      <h4>–ê–ª—å–±–æ–º—ã:</h4>
                      <div className="albums-tags">
                        {result.albums.map((album, i) => (
                          <span key={i} className="album-tag">
                            {album === 'People' && 'üë• '}
                            {album === 'Car' && 'üöó '}
                            {album === 'Animals' && 'üêæ '}
                            {album === 'Nature' && 'üåø '}
                            {album}
                          </span>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      <footer className="app-footer">
        <p>¬© {new Date().getFullYear()} Image Classifier | –í—Å–µ –ø—Ä–∞–≤–∞ –∑–∞—â–∏—â–µ–Ω—ã</p>
      </footer>
    </div>
  );
}

export default App;
