import React, { useState, useEffect } from 'react';
import './App.css';
import { Moon, Sun, Search } from 'lucide-react';

function App() {
  const [files, setFiles] = useState([]);
  const [albums, setAlbums] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [previews, setPreviews] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [search, setSearch] = useState('');
  const [filteredAlbums, setFilteredAlbums] = useState({});

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  useEffect(() => {
    if (!search) {
      setFilteredAlbums(albums);
    } else {
      const query = search.toLowerCase();
      const filtered = {};
      Object.entries(albums).forEach(([albumName, photos]) => {
        const matchingPhotos = photos.filter(photo => photo.name.toLowerCase().includes(query));
        if (albumName.toLowerCase().includes(query) || matchingPhotos.length > 0) {
          filtered[albumName] = matchingPhotos;
        }
      });
      setFilteredAlbums(filtered);
    }
  }, [search, albums]);

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length > 0) {
      setFiles(selectedFiles);
      setError(null);
      const newPreviews = [];
      selectedFiles.forEach(file => {
        const reader = new FileReader();
        reader.onloadend = () => {
          newPreviews.push({
            id: URL.createObjectURL(file),
            url: reader.result,
            name: file.name,
            file
          });
          if (newPreviews.length === selectedFiles.length) {
            setPreviews(newPreviews);
          }
        };
        reader.readAsDataURL(file);
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (files.length === 0) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —Ñ–∞–π–ª—ã');
      return;
    }
    setIsLoading(true);
    setError(null);
    setAlbums({});
    try {
      const formData = new FormData();
      previews.forEach(preview => {
        formData.append('files', preview.file);
      });
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) throw new Error('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞');
      const data = await response.json();
      const albumsData = {};
      data.forEach((result, index) => {
        result.albums.forEach(album => {
          if (!albumsData[album]) albumsData[album] = [];
          albumsData[album].push({
            id: previews[index].id,
            url: previews[index].url,
            name: result.filename
          });
        });
      });
      setAlbums(albumsData);
    } catch (err) {
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={`app-container ${darkMode ? 'dark' : ''}`}>
      <header className="app-header">
        <div className="flex justify-between items-center">
          <div>
            <h1>üì∑ –ö–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ç–æ—Ä –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π</h1>
            <p>–ó–∞–≥—Ä—É–∑–∏—Ç–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–π —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏ –ø–æ –∞–ª—å–±–æ–º–∞–º</p>
          </div>
          <div className="flex gap-4 items-center">
            <div className="relative">
              <Search className="absolute left-2 top-2.5 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="–ü–æ–∏—Å–∫..."
                className="pl-8 pr-4 py-2 rounded-lg border dark:border-gray-700 bg-white dark:bg-gray-800 focus:outline-none"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>
            <button onClick={() => setDarkMode(!darkMode)} className="toggle-theme-button">
              {darkMode ? <Sun /> : <Moon />}
            </button>
          </div>
        </div>
      </header>

      <main className="main-content">
        <div className="upload-section">
          <form onSubmit={handleSubmit} className="upload-form">
            <div className="file-input-container">
              <label className="file-input-label">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept="image/*"
                  className="file-input"
                  multiple
                />
                <span className="file-input-button">–í—ã–±—Ä–∞—Ç—å —Ñ–∞–π–ª—ã</span>
                <span className="file-name">
                  {files.length > 0 ? `${files.length} —Ñ–∞–π–ª(–æ–≤) –≤—ã–±—Ä–∞–Ω–æ` : '–§–∞–π–ª—ã –Ω–µ –≤—ã–±—Ä–∞–Ω—ã'}
                </span>
              </label>
            </div>
            <button
              type="submit"
              disabled={isLoading || files.length === 0}
              className={`upload-button ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? <><span className="spinner"></span>–°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞...</> : '–°–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø–æ –∞–ª—å–±–æ–º–∞–º'}
            </button>
          </form>
          {error && <div className="error-message">{error}</div>}
        </div>

        {previews.length > 0 && (
          <div className="previews-section">
            <h3>–í—ã–±—Ä–∞–Ω–Ω—ã–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è ({previews.length}):</h3>
            <div className="previews-grid">
              {previews.map((preview) => (
                <div key={preview.id} className="preview-item">
                  <img src={preview.url} alt={preview.name} className="preview-image" />
                  <span className="preview-name">{preview.name}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {Object.keys(filteredAlbums).length > 0 && (
          <div className="albums-section">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏</h2>
            <div className="albums-container">
              {Object.entries(filteredAlbums).map(([albumName, photos]) => (
                <div key={albumName} className="album-card">
                  <div className="album-header">
                    <div className="album-icon">
                      {albumName === 'People' && 'üë•'}
                      {albumName === 'Car' && 'üöó'}
                      {albumName === 'Animals' && 'üêæ'}
                      {albumName === 'Nature' && 'üåø'}
                      {!['People', 'Car', 'Animals', 'Nature'].includes(albumName) && 'üìÅ'}
                    </div>
                    <h3 className="album-title">{albumName}</h3>
                    <span className="album-count">{photos.length} —Ñ–æ—Ç–æ</span>
                  </div>
                  <div className="album-photos">
                    {photos.map(photo => (
                      <div key={photo.id} className="photo-item">
                        <img src={photo.url} alt={photo.name} className="album-photo" />
                        <span className="photo-name">{photo.name}</span>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      <footer className="app-footer">
